(205, 1, '2024-05-16 18:33:00', '2024-05-16 21:33:00', '<!-- wp:uagb/container {\"block_id\":\"6bb7cc7c\",\"minHeightDesktop\":531,\"directionDesktop\":\"row\",\"variationSelected\":true,\"columnGapDesktop\":86,\"isBlockRootParent\":true} -->\n<div class=\"wp-block-uagb-container uagb-block-6bb7cc7c alignfull uagb-is-root-container\"><div class=\"uagb-container-inner-blocks-wrap\"><!-- wp:uagb/container {\"block_id\":\"4fee5a1e\",\"widthDesktop\":50,\"widthSetByUser\":true,\"layout\":\"grid\",\"gridRowDesktop\":[{\"default\":\"custom\",\"min\":{\"unit\":\"px\",\"value\":10},\"max\":{\"unit\":\"fr\",\"value\":1},\"custom\":{\"unit\":\"fr\",\"value\":1}},{\"default\":\"custom\",\"min\":{\"unit\":\"px\",\"value\":10},\"max\":{\"unit\":\"fr\",\"value\":1},\"custom\":{\"unit\":\"fr\",\"value\":1}}],\"gridAlignContentDesktop\":\"center\",\"gridJustifyContentDesktop\":\"center\"} -->\n<div class=\"wp-block-uagb-container uagb-layout-grid uagb-block-4fee5a1e\"><!-- wp:heading {\"textAlign\":\"center\",\"level\":1} -->\n<h1 class=\"wp-block-heading has-text-align-center\">Depoimentos</h1>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">1ª Entrevista com os integrantes da Guarda de Congo de São Benedito e Nossa Senhora do Rosário</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:uagb/container {\"block_id\":\"531606ca\",\"gridSettingType\":\"advance\"} -->\n<div class=\"wp-block-uagb-container uagb-block-531606ca\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:uagb/modal {\"block_id\":\"fd7f35ad\",\"defaultTemplate\":true,\"buttonText\":\"Carmelita\",\"modalAlign\":\"center\",\"modalWidth\":1300,\"btnSpacingLink\":false,\"paddingBtnTop\":20,\"paddingBtnBottom\":20,\"paddingBtnLeft\":20,\"paddingBtnRight\":20,\"btnLin